sp_widget.client_script:
function(spModal) {
  /* widget controller */
  var c = this;
	var shared ={};
	c.onBrowseLang= function(size){
	console.log("client");

		//this func is called by a button in html
		shared.currentLge=c.data.currentLge;
		console.log(shared.currentLge.usercheck)
	if(shared.currentLge.usercheck=="survey already submitted")
			{
				alert("survey already submitted");
				return;
			}
		else{


		//we store the currentlage data as obj in shared so tht it availble in embedded widget
		spModal.open({
			//spmodel.open will open a model, and we will pass it followig parametrs
			size:size,
			title:'Please help us improve by taking our short satisfaction survey!!',
			widget:'36ad357a4774b91026847688c26d4342',
		//	widgetInput:{table:'x_698230_my_custom_custom_survey_list'},
			buttons:[
				{label:'${Submit}',primary:true}
			],
			shared:shared//we make shared array available to modal

		}).then(function(){
			alert('updated the record');
			//c.data.selectedLang=shared.selectedLang;//the selected lan is shared with its widget vis share object

			c.server.update();//send the updated data to obj to server script
		});
	//}
	}
}

