sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */


  var c = this;
	var shared =c.widget.options.shared;
  alert((JSON.stringify(c.data.transportdet)))
	c.submit = function(lang,id,sysidoflangrec) 
	{
		//alert(sysidoflangrec)
		c.server.update().then(function() {
			c.data.lang= lang;
			c.data.id=id;
			c.data.sys_id=sysidoflangrec;
			shared.lang=lang
			shared.id=id;
			shared.sys_id=sysidoflangrec;
			//shared object movement from html data to parent widget then to server side
			//	alert("id "+JSON.stringify(shared))

		})

	}


};

