sys_script_client.script:
function onLoad() {
    //Add a UI macro to a non-reference field
    //Set the name of the field in the 'field' variable
    var field = 'name';
    //Append the table name to get the field id
    field = g_form.tableName + '.' + field;
alert(field+"answer");
    try{
    //Create the image element and add to the dom
    $(field).insert({
            after: '<a onclick="doSomething()" name="my_custom_macro" id="my_custom_macro"><img src="topic-list-search.svg" alt="Show Macro" title="Show Macro" style="padding-right:4px"></a>'
        });
    }
    catch(e){
    alert('Error');
    }
}

//onclick event to fire when the image is clicked
function doSomething() {
   alert('You clicked me!');
}
