var RITMUiMacro = Class.create();
RITMUiMacro.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

getVips: function()

{

var usr = gs.getUserDisplayName();

var app = new GlideRecord('sys_user');

gs.info(usr+"userrr");

app.addQuery('name',usr);

app.addQuery('vip',true);

app.query();



 var x=app.getRowCount();
gs.info("x values:"+x);
		if(x==0){
		return 'hide_icon';
		}else{
			return 'show_icon';
		}
    },
    type: 'RITMUiMacro'
});
