var test_gsftsubmit = Class.create();
test_gsftsubmit.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
getRecordCount:function(){
var assignmentgroup = this.getParameter("sysparm_ass");
var assignedto=this.getParameter('sysparm_assto');
	gs.info(assignmentgroup+"assignmentgroup")
var x=new GlideAggregate('problem');
x.addAggregate('COUNT')
x.addQuery('assignment_groupISNOTEMPTY');
x.addQuery('assigned_to',assignedto);
x.addQuery('assignment_group',assignmentgroup);
x.addQuery('state!=107^ORstate=NULL')
x.query();
if(x.next()){
    var y=x.getAggregate('COUNT');
	   return y;
}
},
    type: 'test_gsftsubmit'
});
